#!/bin/bash\n\n# Clear Next.js cache and restart development server\necho \"ðŸ§¹ Clearing Next.js cache and restarting...\"\n\n# Kill any existing Next.js process\necho \"Stopping existing Next.js processes...\"\npkill -f \"next dev\" || true\n\n# Clear Next.js build cache\necho \"Clearing .next cache...\"\nrm -rf .next\n\n# Clear node_modules cache if needed\necho \"Clearing node_modules cache...\"\nnpm cache clean --force 2>/dev/null || true\n\n# Wait a moment\nsleep 2\n\necho \"âœ… Cache cleared! Starting development server...\"\n\n# Start development server\nnpm run dev\n